import { useState } from "react"
//import contactToShow from "../components/contactToShow"
//import addName from "./components/addName"


function App() {

  const [person, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState("")
  const [newNumber, setNewNumber] = useState("")
  const[filter, setFilter] = useState("")

  const Filter = ({filter})=> {
    const filterChange = (event) => {
      setFilter(event.target.value)
    }
    return(<p> filter shown with
      <input value = {filter} onChange ={filterChange}>
      </input> 
    </p>
  )

}
  const contactToShow = false
  ? person
  : person.filter(person => (person.name.toLowerCase()).includes(filter.toLowerCase()))


  const addName = (event) =>{
    event.preventDefault()

    if(person.some((element) => element.name === newName)){
      alert(`${newName} is already added to phonebook.`)
    return
    }

    const nameObject = {
      name: newName,
      number:newNumber,
      key: newName
    }
    setPersons(person.concat(nameObject))
    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }



  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filter ={filter}/> 


    <h3>Add a new</h3>
      <form onSubmit = {addName}>
        <div> name:  <input value={newName} onChange = {handleNameChange}/> </div>
        <div> number:  <input value={newNumber} onChange = {handleNumberChange}/> </div>
        <div>
          <button type ="submit">add</button>
        </div>
      </form>
      <h3>Numbers</h3>
      <ul>
        {contactToShow.map(person => 
         <p key = {person.name}> {person.name} {person.number}</p>
         )}
      </ul>

    </div>
  );
}
export default App;
