import React from "react"
import App from "/Users/ashtonbennett/Desktop/fullstackopen/the_phonebook/src/App.js"
const addName = (event) =>{
    event.preventDefault()
    console.log(App.person,App)
    if(App.person.some((element) => element.name === App.newName)){
      alert(`${App.newName} is already added to phonebook.`)
    return
    }

    const nameObject = {
      name: App.newName,
      number:App.newNumber,
      key: App.newName
    }
    App.setPersons(App.person.concat(nameObject))
    App.setNewName('')
    App.setNewNumber('')
  }

  export default addName
