import { useState } from "react"
function App() {

  const [person, setPersons] = useState([{ name: "Arto Hellas", number: "040-1234567", key: "Arto Hellas"}])
  const [newName, setNewName] = useState("")
  const [newNumber, setNewNumber] = useState("")

  console.log("U here--->",person.name, newName)
  const addName = (event) =>{
    event.preventDefault()

    if(person.some((element) => element.name === newName)){
      alert(`${newName} is already added to phonebook.`)
    return
    }

    const nameObject = {
      name: newName,
      number:newNumber,
      key: newName
    }
    setPersons(person.concat(nameObject))
    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  console.log(person)
  return (
    <div>
      <h2>Phonebook</h2>
      <p> filter shown with<input></input> </p>
    <h2>add a new</h2>
      <form onSubmit = {addName}>
        <div> name:  <input value={newName} onChange = {handleNameChange}/> </div>
        <div> number:  <input value={newNumber} onChange = {handleNumberChange}/> </div>
        <div>
          <button type ="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {person.map(person => 
         <p key = {person.name}>{person.name} {person.number}</p>
         )}
      </ul>

    </div>
  );
}

export default App;
