import { useEffect, useState } from "react"
import contactService from './services/contacts'

const Filter = ({filter,filterChange})=> {
  return(
  <p> filter shown with
    <input value = {filter} onChange ={filterChange}>
    </input> 
  </p>
)}

const PersonForm = ({addName,newName,newNumber,handleNameChange,handleNumberChange}) => {
  return(

  <form onSubmit = {addName}>
    <div> name:  <input value={newName} onChange = {handleNameChange}/> </div>
    <div> number:  <input value={newNumber} onChange = {handleNumberChange}/> </div>
    <div>
      <button type ="submit">add</button>
    </div>
  </form>
  )

}




const Contacts = ({person,filter,removeContact}) => {
  console.log(person)
  const contactToShow = false
  ? person
  : person.filter(person => (person.name.toLowerCase()).includes(filter.toLowerCase()))
  return(
    <ul>
    {contactToShow.map(person => 
     <p key = {person.name}> {person.name} {person.number}  
       <button id="007" onClick={()=>removeContact(person.id,person.name)}>delete</button>
     </p>
     )}
  </ul>  
  )
}

function App() {

  const [person, setPersons] = useState([])
  const [newName, setNewName] = useState("")
  const [newNumber, setNewNumber] = useState("")
  const[filter, setFilter] = useState("")

  const hook = () => {
    contactService
    .getAll()
    .then(initialContacts => {
       setPersons(initialContacts)
    })
 }
 useEffect(hook,[])

  const addName = (event) =>{
    event.preventDefault()

    if(person.some(element => element.name === newName)){

       if(window.confirm(`${newName} is already added to phonebook, replace the old number with a new one?`)){
        const contactToUpdate = person.filter(contact=> contact.name === newName)
           const updatedContact = {...contactToUpdate, number: newNumber}
           console.log(updatedContact)
        return(
          contactService
          .update(contactToUpdate[0].id, updatedContact)
          .then(contact=>{
            setPersons(contact)

          })

        )
      }
    }



    const nameObject = {
      name: newName,
      number:newNumber,
      key: newName
    }
    contactService
    .create(nameObject)
    .then(returnedContact =>{
      setPersons(person.concat(returnedContact))
      setNewName('')
      setNewNumber('')
    })
  }

  const removeContact = (id, person) => {
    if(window.confirm(`Delete ${person} from phonebook?`)){

      return (contactService
    .deleteContact(id),
    contactService
    .getAll()
    .then(initialContacts => {
       setPersons(initialContacts)

    })
      )
  }
  }


  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const filterChange = (event) => {
    setFilter(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <Filter filter={filter} filterChange={filterChange}/> 

      <PersonForm addName={addName} newName = {newName} newNumber ={newNumber} handleNameChange={handleNameChange} handleNumberChange={handleNumberChange} />

      <h3>Numbers</h3>

      <Contacts filter={filter} person={person} name={person.name} number={person.number} removeContact ={removeContact}/>

    </div>
  );
}
export default App;
